// In Web.config or startup.cs, retrieve settings from environment variables\npublic class Startup\n{\n    public void Configuration(IAppBuilder app)\n    {\n        string awsAccessKey = Environment.GetEnvironmentVariable("AWS_ACCESS_KEY_ID");\n        string awsSecretKey = Environment.GetEnvironmentVariable("AWS_SECRET_ACCESS_KEY");\n\n        // Initialize AWS clients using the retrieved keys\n        var s3Client = new AmazonS3Client(awsAccessKey, awsSecretKey, RegionEndpoint.USEast1);\n    }\n}