public static bool IsValidPassword(string password)\n{\n    string tempUsername = Guid.NewGuid().ToString();\n    try\n    {\n        Membership.CreateUser(tempUsername, password, "dummy@example.com");\n        return true;\n    }\n    catch (MembershipCreateUserException)\n    {\n        return false;\n    }\n    finally\n    {\n        Membership.DeleteUser(tempUsername);\n    }\n}