using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    private BindingSource bindingSource = new BindingSource();\n    private DataGridView dataGridView = new DataGridView();\n\n    public MyForm()\n    {\n        // Setup UI\n        dataGridView.Dock = DockStyle.Fill;\n        Controls.Add(dataGridView);\n\n        using (var context = new YourContext())\n        {\n            // Fetch data into a local collection or observable collection\n            var entities = context.YourEntities.Local.ToBindingList();\n            \n            // Bind the binding source to the list of entities\n            bindingSource.DataSource = entities;\n\n            // Set the grid's data source to the binding source\n            dataGridView.DataSource = bindingSource;\n        }\n\n        // Save changes when needed\n        btnSave.Click += delegate\n        {\n            using (var context = new YourContext())\n            {\n                foreach (YourEntity entity in bindingSource)\n                {\n                    context.YourEntities.Attach(entity);\n                    context.Entry(entity).State = EntityState.Modified;\n                }\n                context.SaveChanges();\n            }\n        };\n    }\n}