using System;\nusing System.IO.Ports;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string portName = "COM3"; // Change this to your printer's COM port\n        int baudRate = 9600; // Ensure this matches the printer settings\n\n        using (SerialPort serialPort = new SerialPort(portName, baudRate))\n        {\n            try\n            {\n                serialPort.Open();\n\n                // ESC/POS commands for Code-A Barcode\n                byte[] barcodeCommand = { 0x1B, 0x62, 0x49 }; // GS b I Select Code 128\n                string data = "12345"; // Data to encode in the barcode\n\n                serialPort.Write(barcodeCommand, 0, barcodeCommand.Length);\n                serialPort.Write(Encoding.UTF8.GetBytes(data));\n                serialPort.WriteByte(0x00); // Null character to indicate end of barcode data\n\n                Console.WriteLine("Barcode printed successfully.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error: {ex.Message}");\n            }\n            finally\n            {\n                if (serialPort.IsOpen) serialPort.Close();\n            }\n        }\n    }\n}