protected void Page_Load(object sender, EventArgs e)\n{\n    // Example data from database\n    List<Advertisement> advertisements = GetAdvertisementsFromDatabase();\n\n    int maxRowSpan = 0;\n    StringBuilder htmlContent = new StringBuilder();\n    \n    foreach (var ad in advertisements)\n    {\n        htmlContent.AppendFormat("<div style='grid-area: span {0} / span {1}; background-image: url({2});'></div>", \n            ad.Height, ad.Width, ad.ImageURL);\n        maxRowSpan = Math.Max(maxRowSpan, ad.Height);\n    }\n\n    GridContainer.InnerHtml = htmlContent.ToString();\n}\n\nprivate List<Advertisement> GetAdvertisementsFromDatabase()\n{\n    // Example method to get data from database\n    return new List<Advertisement>\n    {\n        new Advertisement { AdvertisementID = 1, Width = 2, Height = 1 },\n        new Advertisement { AdvertisementID = 2, Width = 1, Height = 1 },\n        new Advertisement { AdvertisementID = 3, Width = 2, Height = 2 },\n        new Advertisement { AdvertisementID = 4, Width = 1, Height = 1 }\n    };\n}\n\npublic class Advertisement\n{\n    public int AdvertisementID { get; set; }\n    public string AdvertiserID { get; set; }\n    public string Category { get; set; }\n    public string ImageURL { get; set; }\n    public int Width { get; set; }\n    public int Height { get; set; }\n}