using System;\nusing Microsoft.PointOfService;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        PosExplorer posExplorer = new PosExplorer();\n        \n        DeviceInfo cashDrawerInfo = null;\n        try\n        {\n            cashDrawerInfo = posExplorer.GetDevice(DeviceType.CashDrawer, "CashDrawerLogicalName");\n        }\n        catch (PosControlException ex)\n        {\n            Console.WriteLine("Unable to find the Cash Drawer device: " + ex);\n            return;\n        }\n\n        CashDrawer cashDrawer = (CashDrawer)posExplorer.CreateInstance(cashDrawerInfo);\n\n        try\n        {\n            cashDrawer.Open();\n            cashDrawer.Claim(1000);\n            cashDrawer.DeviceEnabled = true;\n\n            if (!cashDrawer.Status.ToString().Equals("Closed"))\n            {\n                cashDrawer.OpenDrawer();\n                Console.WriteLine("Cash drawer opened successfully.");\n            }\n\n            // Always make sure to close out the device properly\n            cashDrawer.DeviceEnabled = false;\n            cashDrawer.Release();\n            cashDrawer.Close();\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error working with Cash Drawer: " + ex.Message);\n        }\n    }\n}