using System;\nusing System.IO.Pipes;\nusing System.Text;\n\npublic class ChildProcess {\n\n    public static void Main(string[] args) {\n        var pipeName = "MyCustomPipe";\n        using (NamedPipeClientStream pipeClient =\n            new NamedPipeClientStream(".", pipeName, PipeDirection.Out))\n        {\n            pipeClient.Connect();\n\n            using (StreamWriter writer = new StreamWriter(pipeClient))\n            {\n                // Simulate some work and send status updates\n                for (int i = 0; i < 10; i++)\n                {\n                    writer.WriteLine("Child Process Status " + i);\n                    writer.Flush();\n                    System.Threading.Thread.Sleep(500); // Simulate time-consuming task\n                }\n            }\n        }\n    }\n}