using Microsoft.AspNetCore.Mvc;\n\n[ApiController]\n[Route("[controller]")]\npublic class SmsController : ControllerBase\n{\n    [HttpPost("receive")]\n    public IActionResult ReceiveSms([FromForm] string From, [FromForm] string Body)\n    {\n        // Log the message or process it as needed\n        Console.WriteLine($"Received SMS from {From} with body: {Body}");\n\n        // Respond back to indicate successful receipt of SMS\n        return Ok();\n    }\n}