using System;\nusing System.IO.Pipes;\n\npublic class MainForm : Form {\n    private NamedPipeServerStream pipeServer;\n\n    public MainForm() {\n        InitializeUI();\n        StartPipeServer();\n    }\n\n    private void StartPipeServer() {\n        var pipeName = "MyCustomPipe";\n        new Thread(() => {\n            using (pipeServer = new NamedPipeServerStream(pipeName, PipeDirection.In))\n            {\n                pipeServer.WaitForConnection();\n\n                using (StreamReader sr = new StreamReader(pipeServer))\n                {\n                    string message;\n                    while ((message = sr.ReadLine()) != null)\n                    {\n                        // Process incoming messages and update UI\n                        BeginInvoke((MethodInvoker)delegate { UpdateStatus(message); });\n                    }\n                }\n            }\n        }).Start();\n    }\n\n    private void UpdateStatus(string statusMessage) {\n        // Update your UI with the received message\n        MessageBox.Show(statusMessage);\n    }\n\n    private void InitializeUI() {\n        // Set up your form, buttons, labels, etc.\n    }\n}