using System;\nusing System.Net;\nusing System.Xml.Linq;\n\npublic string GetArtistThumbnail(string artistName)\n{\n    var request = WebRequest.Create("http://ws.audioscrobbler.com/2.0/?method=artist.getinfo&artist=" + Uri.EscapeDataString(artistName) +\n                                        "&api_key=" + APIKey) as HttpWebRequest;\n\n    using (var response = request.GetResponse() as HttpWebResponse)\n    {\n        using (var reader = new System.IO.StreamReader(response.GetResponseStream()))\n        {\n            var xmlContent = XDocument.Load(reader);\n            var thumbnailUrl = (string)(xmlContent.Descendants("image")\n                .FirstOrDefault(img => (string)img.Attribute("size") == "medium"));\n\n            return thumbnailUrl ?? string.Empty;\n        }\n    }\n}