[Authorize]\npublic class ApiKeyAttribute : AuthorizeAttribute\n{\n    protected override bool AuthorizeCore(HttpContextBase httpContext)\n    {\n        var request = httpContext.Request;\n        string apiKey = request.QueryString["apiKey"] ?? request.Headers["Authorization"];\n\n        // Validate the API key here\n        return IsApiKeyValid(apiKey);\n    }\n\n    private static bool IsApiKeyValid(string apiKey)\n    {\n        // Implement your validation logic here\n        return !string.IsNullOrEmpty(apiKey) && apiKey == "valid-api-key";\n    }\n}