public string Encrypt(byte[] PlainTextBytes, byte[] KeyBytes, byte[] InitialVectorBytes)\n{\n    RijndaelManaged SymmetricKey = new RijndaelManaged();\n    SymmetricKey.Mode = CipherMode.CBC; // Use CBC mode\n    SymmetricKey.Padding = PaddingMode.PKCS7; // PKCS7 is compatible with PKCS5 for block sizes of 8 bytes or more\n    ICryptoTransform Encryptor = SymmetricKey.CreateEncryptor(KeyBytes, InitialVectorBytes);\n    MemoryStream MemStream = new MemoryStream();\n    CryptoStream CryptoStream = new CryptoStream(MemStream, Encryptor, CryptoStreamMode.Write);\n    CryptoStream.Write(PlainTextBytes, 0, PlainTextBytes.Length);\n    CryptoStream.FlushFinalBlock();\n    byte[] CipherTextBytes = MemStream.ToArray();\n    MemStream.Close();\n    CryptoStream.Close();\n    return ByteToHexConversion(CipherTextBytes); // Ensure this method converts bytes to hexadecimal correctly\n}