protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        var userRole = GetCurrentUserRole(); // Assume this method fetches the current user's role\n\n        switch (userRole)\n        {\n            case "Admin":\n                adminView.Visible = true;\n                BindDirectorsGrid();\n                break;\n\n            case "Director":\n                pmView.Visible = true;\n                BindProjectManagersForDirector(GetCurrentDirectorsId()); // Assume this method fetches the director's ID\n                break;\n            \n            default:\n                // Handle other roles or unauthorized access\n                break;\n        }\n    }\n}\n\nprotected void DirectorsGrid_RowCommand(object sender, GridViewCommandEventArgs e)\n{\n    if (e.CommandName == "ViewProjectManagers")\n    {\n        var directorId = Convert.ToInt32(e.CommandArgument);\n        \n        pmView.Visible = true;\n        adminView.Visible = false;\n\n        BindProjectManagersForDirector(directorId);\n    }\n}