using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    public MyForm()\n    {\n        Label label = new Label\n        {\n            Text = "This is some example text that will wrap within the fixed dimensions of the label.",\n            AutoSize = false,\n            Width = 200, // Fixed width\n            Height = 100, // Fixed height\n            TextAlign = ContentAlignment.TopLeft,\n            AutoEllipsis = true, // Optional: Show ellipsis if text is too long to fit\n            UseMnemonic = false, // Optional: Disable mnemonic (e.g., '&')\n            WordWrap = true, // Enable word wrapping\n        };\n        \n        this.Controls.Add(label);\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MyForm());\n    }\n}