public interface IAddressInfo {}\npublic interface IEmailInfo {}\npublic interface IPhoneInfo {}\n\npublic class Address : IAddressInfo {}\npublic class Email : IEmailInfo {}\npublic class Phone : IPhoneInfo {}\n\npublic class ContactManager \n{\n    private List<Address> m_Address = new List<Address>();\n    private List<Email> m_Email = new List<Email>();\n    private List<Phone> m_Phone = new List<Phone>();\n\n    public void Add<T>(T obj) where T : IAddressInfo, IEmailInfo, IPhoneInfo\n    {\n        // You need to ensure that the object can logically implement all these interfaces.\n        if (obj is Address address)\n            m_Address.Add(address);\n        else if (obj is Email email)\n            m_Email.Add(email);\n        else if (obj is Phone phone)\n            m_Phone.Add(phone);\n    }\n}