using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass MyClass\n{\n    public MyClass(int value)\n    {\n        this.Value = value;\n    }\n\n    public int Value { get; set; }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        List<int> sortedValuesList = new List<int> { 5, 3, 9, 1 };\n        List<MyClass> objectList = new List<MyClass>\n        {\n            new MyClass(3),\n            new MyClass(5),\n            new MyClass(1),\n            new MyClass(9),\n            new MyClass(3) // Duplicate value\n        };\n\n        var valueToIndexMap = sortedValuesList\n            .Select((value, index) => new { Value = value, Index = index })\n            .Where(x => x.Value != null)\n            .ToDictionary(x => x.Value, x => x.Index);\n\n        objectList.Sort((x, y) =>\n        {\n            int indexX = valueToIndexMap[x.Value];\n            int indexY = valueToIndexMap[y.Value];\n            return indexX.CompareTo(indexY);\n        });\n\n        foreach (var obj in objectList)\n        {\n            Console.WriteLine(obj.Value);\n        }\n    }\n}