// Install-Microsoft.Web.RedisSessionStateProvider via NuGet package manager\n\npublic void Session_Start()\n{\n    // Configure Redis session state provider settings in web.config\n}\n\n<configuration>\n  <system.web>\n    <sessionState mode="Custom" customProvider="MySessionStateStore">\n      <providers>\n        <add name="MySessionStateStore" \n             type="Microsoft.Web.Redis.RedisSessionStateProvide"\n             host = "your-redis-host" \n             accessKey = "your-access-key"\n             ssl = "true" />\n      </providers>\n    </sessionState>\n  </system.web>\n</configuration>