protected void ButtonGenerateFile_Click(object sender, EventArgs e)\n{\n    // Your file generation logic here\n\n    // Set a flag in Session State to indicate file has been generated\n    Session["FileGenerated"] = true;\n\n    // Redirect to same page or another page with success query string\n    Response.Redirect(Request.Url.AbsolutePath + "?filegenerated=1");\n}\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Check if the session state has the flag that indicates file generation and message shows only once\n        if (Session["FileGenerated"] != null && Convert.ToBoolean(Session["FileGenerated"]))\n        {\n            lblMessage.Text = "Your file has been successfully generated!";\n            Session.Remove("FileGenerated");\n        }\n    }\n}