using Microsoft.Office.Core;\n\nvoid AddTrustedLocation(string path)\n{\n    var application = new Microsoft.Office.Interop.Word.Application();\n    var trustedLocations = application.TrustLocations;\n    try\n    {\n        trustedLocations.Add(path, true);\n        Console.WriteLine("Path added as a Trust Location.");\n    }\n    catch (System.Runtime.InteropServices.COMException ex)\n    {\n        Console.WriteLine($@"Error adding path to Trust Locations: {ex.Message}");\n    }\n    finally\n    {\n        Marshal.ReleaseComObject(trustedLocations);\n        application.Quit();\n        Marshal.ReleaseComObject(application);\n    }\n}