using System.Collections;\nusing System.Reflection;\n\npublic int SaveCollection(DictionaryBase objCollection, string TableName, string spSave)\n{\n    PropertyInfo propInfo;\n\n    // Loop through each entry in the dictionary\n    foreach (DictionaryEntry entry in objCollection)\n    {\n        // Cast the Value to an Object since it's the type you expect your data class to be\n        object objClass = entry.Value;\n        \n        // Get the PropertyInfo for the property "TableFieldName"\n        propInfo = objClass.GetType().GetProperty("TableFieldName");\n        \n        if (propInfo != null)\n        {\n            // Assuming the property is accessible and of a readable type,\n            // you can get the value like this:\n            object propertyValue = propInfo.GetValue(objClass);\n            \n            // Now you can use `propertyValue` to set your SQLParameter\n        }\n    }\n\n    return 0; // Adjust return type as necessary\n}