using HtmlAgilityPack;\n\npublic static string StandardizeHtml(string html)\n{\n    var doc = new HtmlDocument();\n    doc.LoadHtml(html);\n\n    // Replace <b> with <strong>\n    foreach (var bNode in doc.DocumentNode.SelectNodes("//b"))\n    {\n        if (bNode != null)\n        {\n            var strongNode = HtmlNode.CreateNode("<strong></strong>");\n            strongNode.AppendChildren(bNode.ChildNodes);\n            bNode.ReplaceWith(strongNode);\n        }\n    }\n\n    // Replace <span style="font-weight:bold"> with <strong>\n    foreach (var spanNode in doc.DocumentNode.SelectNodes("//span[@style='font-weight:bold']"))\n    {\n        if (spanNode != null)\n        {\n            var strongNode = HtmlNode.CreateNode("<strong></strong>");\n            strongNode.AppendChildren(spanNode.ChildNodes);\n            spanNode.ReplaceWith(strongNode);\n        }\n    }\n\n    return doc.DocumentNode.OuterHtml;\n}\n\n// Usage\nstring inputHtml = "<b>die Bundesliga Mannschaften</b><span style=\"font-weight:bold\">die Bundesliga Mannschaften</span>";\nstring standardizedHtml = StandardizeHtml(inputHtml);\nConsole.WriteLine(standardizedHtml);