using (var db = this.AppManager.DB)\n{\n    Thread t = this.AppManager.ForumManager.GetThread(id);\n    \n    // Refresh the thread from the database to ensure you have the latest state\n    db.Refresh(RefreshMode.OverwriteCurrentValues, t);\n\n    t.Views = t.Views + 1;\n    \n    try\n    {\n        db.SubmitChanges();\n    }\n    catch (ChangeConflictException)\n    {\n        // Log or handle conflict as needed\n        db.ChangeConflicts.ResolveAll(RefreshMode.KeepChanges);\n        db.SubmitChanges();\n    }\n}