protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        ViewState["ControlCount"] = 0; // Initialize on first load\n    }\n    \n    int controlCount = (int)ViewState["ControlCount"];\n    for (int i = 0; i < controlCount; i++)\n    {\n        CreateControls(i);\n    }\n}\n\nprotected void LinkButton_Click(object sender, EventArgs e)\n{\n    int currentCount = (int)ViewState["ControlCount"];\n    CreateControls(currentCount);\n    ViewState["ControlCount"] = currentCount + 1;\n}\n\nprivate void CreateControls(int index)\n{\n    DropDownList newDropdownlist = new DropDownList();\n    newDropdownlist.ID = "ddl" + index;\n    panel.Controls.Add(newDropdownlist);\n\n    CheckBox newChkbox = new CheckBox();\n    newChkbox.ID = "chk" + index;\n    panel.Controls.Add(newChkbox);\n\n    TextBox txt = new TextBox();\n    txt.ID = "txtPhoneValue" + index;\n    panel.Controls.Add(txt);\n}