using System;\nusing System.IO;\nusing System.Reflection;\n\npublic static class TestUtils\n{\n    public static Stream LoadResource(string resourceName)\n    {\n        // Get a reference to the currently executing assembly.\n        Assembly assembly = Assembly.GetExecutingAssembly();\n        // Load resource stream.\n        Stream stream = assembly.GetManifestResourceStream(resourceName);\n\n        if (stream == null)\n        {\n            throw new FileNotFoundException($"Error loading '{resourceName}'");\n        }\n\n        return stream;\n    }\n\n    public static string LoadResourceAsString(string resourceName)\n    {\n        using (Stream stream = LoadResource(resourceName))\n        using (StreamReader reader = new StreamReader(stream))\n        {\n            return reader.ReadToEnd();\n        }\n    }\n}\n\n// Usage\npublic class Program\n{\n    public static void Main()\n    {\n        try\n        {\n            // Use the full type name as the root.\n            string resourceName = "YourNamespace.resourcelocation";\n            string content = TestUtils.LoadResourceAsString(resourceName);\n            Console.WriteLine(content);\n        }\n        catch (FileNotFoundException e)\n        {\n            Console.Error.WriteLine(e.Message);\n        }\n    }\n}