using System;\nusing System.IO;\nusing PdfSharp.Drawing;\nusing PdfSharp.Pdf;\nusing TheArtOfDev.HtmlRenderer.PdfSharp;\n\npublic class HtmlToPdfConverter\n{\n    public static void ConvertHtmlToPdf(string htmlContent, string filePath)\n    {\n        // Create a new PDF document\n        using (PdfDocument pdf = new PdfDocument())\n        {\n            // Create an empty page \n            PdfPage page = pdf.AddPage();\n\n            // Get an XGraphics object for drawing\n            XGraphics gfx = XGraphics.FromPdfPage(page);\n\n            // Render HTML content\n            XRect rect = new XRect(0, 0, page.Width, page.Height);\n            HtmlRenderer.Render(gfx, htmlContent, rect);\n\n            // Save the PDF document to a file\n            pdf.Save(filePath);\n        }\n    }\n\n    public static void Main()\n    {\n        string htmlContent = "<p>Your HTML content here. e.g., from TinyMCE editor.</p>";\n        string filePath = "output.pdf";\n\n        ConvertHtmlToPdf(htmlContent, filePath);\n\n        Console.WriteLine("PDF created successfully.");\n    }\n}