using System;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        var numbers = Enumerable.Range(0, 1000);\n\n        // Using AsParallel to parallelize the query\n        var parallelResult = from n in numbers.AsParallel()\n                             select ExpensiveComputation(n);\n\n        foreach (var result in parallelResult)\n        {\n            Console.WriteLine(result);\n        }\n    }\n\n    static int ExpensiveComputation(int n)\n    {\n        // Simulate a task that takes some time to compute\n        System.Threading.Thread.Sleep(10);\n        return n * n;\n    }\n}