[Test]\npublic void GeneratedSqlShouldReturnExpectedResult() {\n    // Arrange\n    var generator = new SQLCodeGenerator();\n    using (var connection = new SqlConnection(_connectionString)) {\n        connection.Open();\n        \n        var createTableSQL = "CREATE TABLE MyObject(Name NVARCHAR(25), Surname NVARCHAR(25), Age INT)";\n        using(var command = new SqlCommand(createTableSQL, connection)) {\n            command.ExecuteNonQuery();\n        }\n        \n        // Act\n        var insertSql = generator.GenerateInsertStatement(new MyObject { Name = "John", Surname = "Doe", Age = 30 });\n        using (var command = new SqlCommand(insertSql, connection)) {\n            command.Parameters.AddWithValue("@Name", "John");\n            command.Parameters.AddWithValue("@Surname", "Doe");\n            command.Parameters.AddWithValue("@Age", 30);\n            command.ExecuteNonQuery();\n        }\n        \n        var selectSQL = generator.GenerateSelectStatement(new MyObject { Name = "John" });\n        using (var command = new SqlCommand(selectSQL, connection)) {\n            command.Parameters.AddWithValue("@Name", "John");\n            var reader = command.ExecuteReader();\n            Assert.IsTrue(reader.Read());\n            Assert.AreEqual("Doe", reader["Surname"].ToString());\n            Assert.AreEqual(30, reader["Age"]);\n        }\n    }\n}