// Example Test Case Structure\npublic class SqlGeneratorTests {\n    [Test]\n    public void GenerateSelectQuery_ShouldReturnExpectedSql() {\n        // Arrange\n        var generator = new SQLCodeGenerator();\n        var expectedSql = "SELECT Name, Surname, Age FROM MyObject WHERE Name = @Name";\n        \n        // Act\n        var actualSql = generator.GenerateSelectStatement(new MyObject { Name = "JohnDoe" });\n        \n        // Assert\n        Assert.AreEqual(expectedSql, actualSql);\n    }\n}