using System;\nusing Act.Framework;\n\nnamespace ActExporter\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Initialize a new instance of the ActFramework\n            ActFramework framework = new ActFramework();\n\n            try\n            {\n                // Connect to an ACT!? database\n                string connectionString = "your-connection-string-here";\n                bool isConnected = framework.LogOnServer("Diana", connectionMode.LogonWindows, connectionString, "act7_sql");\n                \n                if (isConnected)\n                {\n                    // Access the contacts table or any other relevant data\n                    var contactsTable = framework.Contacts.GetContacts();\n\n                    // Iterate over each contact and export their details\n                    foreach (var contact in contactsTable)\n                    {\n                        Console.WriteLine($"Name: {contact.FullName}, Email: {contact.EmailAddress}");\n                        // Add your export logic here\n                    }\n\n                    // Log off the server when done\n                    framework.LogoffServer();\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n}