using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string sourcePath = @"C:\Your\Very\Long\Path\That\Exceeds\The\Limit\FileName.txt";\n        string destinationPath = @"D:\Another\Very\Long\Path\Destination\Folder";\n\n        // Use the long path format\n        string extendedSourcePath = $"\\\\?\\{sourcePath}";\n        string extendedDestinationPath = $"\\\\?\\{destinationPath}\\{new FileInfo(sourcePath).Name}";\n\n        try\n        {\n            File.Copy(extendedSourcePath, extendedDestinationPath);\n            Console.WriteLine("File copied successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}