using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        var items = new List<List<Item>>\n        {\n            new List<Item> { new Item { ID = 1, Amount = 4 }, new Item { ID = 2, Amount = 7 } },\n            new List<Item> { new Item { ID = 1, Amount = 2 }, new Item { ID = 2, Amount = 4 } },\n            new List<Item> { new Item { ID = 1, Amount = 5 }, new Item { ID = 2, Amount = 3 } }\n        };\n\n        var result = items\n            .SelectMany(childCollection => childCollection) // Flatten the list of lists\n            .GroupBy(item => item.ID) // Group by ID\n            .Select(group => new\n            {\n                ID = group.Key,\n                Avg = Math.Round(group.Average(item => item.Amount), 2) // Calculate average and round to 2 decimal places\n            });\n\n        foreach (var item in result)\n        {\n            Console.WriteLine($"ID: {item.ID}, Avg: {item.Avg}");\n        }\n    }\n\n    public class Item\n    {\n        public int ID { get; set; }\n        public double Amount { get; set; }\n    }\n}