using System;\nusing System.IO;\nusing System.Windows.Forms;\n\npublic class CustomFileDialog : Form {\n    private Button selectButton;\n    private TextBox filePathTextBox;\n    private OpenFileDialog openFileDialog;\n\n    public CustomFileDialog() {\n        this.Text = "Select Document";\n\n        // Configure the text box to show selected file path.\n        filePathTextBox = new TextBox();\n        filePathTextBox.Dock = DockStyle.Top;\n        filePathTextBox.ReadOnly = true;\n        \n        // Configure open file dialog.\n        openFileDialog = new OpenFileDialog {\n            Filter = "All Files|*.*",\n            Title = "Select Document"\n        };\n\n        // Configure the button to act as 'Open' but labeled as 'Select'.\n        selectButton = new Button();\n        selectButton.Text = "Select";\n        selectButton.Dock = DockStyle.Bottom;\n        selectButton.Click += SelectButton_Click;\n\n        this.Controls.Add(filePathTextBox);\n        this.Controls.Add(selectButton);\n\n    }\n\n    private void SelectButton_Click(object sender, EventArgs e) {\n        if (openFileDialog.ShowDialog() == DialogResult.OK) {\n            filePathTextBox.Text = openFileDialog.FileName;\n        }\n    }\n\n    [STAThread]\n    public static void Main() {\n        Application.EnableVisualStyles();\n        Application.Run(new CustomFileDialog());\n    }\n}