private byte[] GetImageDataFromStorage(string userId, string imageName)\n{\n    // Generate a unique key for this image\n    string cacheKey = $"image_{userId}_{imageName}";\n\n    if (HttpRuntime.Cache[cacheKey] == null)\n    {\n        // Simulate fetching image data from a secure storage location\n        byte[] imageData = FetchImageFromSecureLocation(userId, imageName);\n\n        // Store the retrieved bytes in the ASP.NET Cache\n        HttpRuntime.Cache.Insert(cacheKey, imageData, null, DateTime.UtcNow.AddHours(1), System.Web.Caching.Cache.NoSlidingExpiration);\n    }\n\n    return (byte[])HttpRuntime.Cache[cacheKey];\n}