using Microsoft.Win32;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string[] keys = {\n            @"SOFTWARE\Microsoft\VisualStudio\9.0\VC\Runtimes\x86",\n            @"SOFTWARE\Wow6432Node\Microsoft\VisualStudio\9.0\VC\Runtimes\x86"\n        };\n\n        const string versionToCheck = "8.0.50727.4053"; // Version 8.0 SP1 RTM\n\n        foreach (string key in keys)\n        {\n            using (RegistryKey rk = Registry.LocalMachine.OpenSubKey(key))\n            {\n                if (rk != null)\n                {\n                    object version = rk.GetValue("Version");\n                    if (version != null && version.ToString() == versionToCheck)\n                    {\n                        Console.WriteLine("Visual C++ 2005 SP1 Redistributable is installed.");\n                        return;\n                    }\n                }\n            }\n        }\n\n        Console.WriteLine("Visual C++ 2005 SP1 Redistributable is not installed.");\n    }\n}