using System;\nusing System.Net.NetworkInformation;\n\nclass Program\n{\n    static void Main()\n    {\n        NetworkInterface[] nics = NetworkInterface.GetAllNetworkInterfaces();\n        foreach (NetworkInterface nic in nics)\n        {\n            Console.WriteLine("ID: {0}", nic.Id);\n            Console.WriteLine("Description: " + nic.Description);\n            Console.WriteLine("Type: {0}", nic.NetworkInterfaceType);\n            Console.WriteLine("Speed: {0} bps", nic.Speed); // Speed returns the bandwidth of the network in bits per second.\n            Console.WriteLine();\n        }\n    }\n}