private static readonly MemoryCache _memoryCache = new MemoryCache(new MemoryCacheOptions());\n\n    // Adding to cache\n    var entryOptions = new MemoryCacheEntryOptions()\n        .SetSlidingExpiration(TimeSpan.FromMinutes(5));\n\n    _memoryCache.Set(userId, userSuggestList, entryOptions);\n\n    // Retrieving from cache\n    if (_memoryCache.TryGetValue(userId, out var cachedUserSuggestions))\n    {\n        return (List<string>)cachedUserSuggestions;\n    }