using System;\nusing System.IO;\nusing System.Data;\nusing System.Data.SqlServerCe;\n\npublic class ExportUtility\n{\n    public void ExportDatabaseToCsv(string sdfFilePath, string outputPath)\n    {\n        using (SqlCeConnection connection = new SqlCeConnection($"Data Source='{sdfFilePath}';"))\n        {\n            connection.Open();\n            \n            using (SqlCeCommand cmd = connection.CreateCommand())\n            {\n                cmd.CommandText = "SELECT * FROM YourTableName"; // Replace 'YourTableName' with your actual table name\n                \n                using (SqlCeDataReader reader = cmd.ExecuteReader())\n                {\n                    DataTable dataTable = new DataTable();\n                    dataTable.Load(reader);\n                    \n                    // Write the data from DataTable to CSV\n                    StringBuilder csvContent = new StringBuilder();\n\n                    for (int i = 0; i < dataTable.Columns.Count; i++)\n                    {\n                        csvContent.Append(dataTable.Columns[i]);\n                        if (i != dataTable.Columns.Count - 1)\n                            csvContent.Append(",");\n                    }\n                    csvContent.AppendLine(); // New line after headers\n\n                    foreach (DataRow row in dataTable.Rows)\n                    {\n                        for (int i = 0; i < dataTable.Columns.Count; i++)\n                        {\n                            csvContent.Append(row[i]);\n                            if (i != dataTable.Columns.Count - 1)\n                                csvContent.Append(",");\n                        }\n                        csvContent.AppendLine();\n                    }\n\n                    File.WriteAllText(outputPath, csvContent.ToString());\n                }\n            }\n        }\n    }\n}