using System;\nusing System.IO;\nusing DinkToPdf;\nusing DinkToPdf.Contracts;\n\npublic class HtmlToPdfConverter\n{\n    private static IConverter _converter;\n\n    public static void ConvertHtmlToFile(string htmlContent, string filePath)\n    {\n        _converter = new SynchronizedConverter(new PdfTools());\n        var globalSettings = new GlobalSettings\n        {\n            ColorMode = ColorMode.Color,\n            Orientation = Orientation.Portrait,\n            PaperSize = PaperKind.A4,\n        };\n\n        var objectSettings = new ObjectSettings\n        {\n            PagesCount = true,\n            HtmlContent = htmlContent,\n            WebSettings = { DefaultEncoding = "utf-8" },\n            HeaderSettings = { Right = "Page [page] of [toPage]", Line = true, Spacing = 2.812 },\n            FooterSettings = { Line = true, Spacing = 2.812, Center = "Report Sharp HTML to PDF with CSS, Images and more!" }\n        };\n\n        var pdfDocument = new HtmlToPdfDocument()\n        {\n            GlobalSettings = globalSettings,\n            Objects = { objectSettings }\n        };\n\n        var file = _converter.Convert(pdfDocument);\n        File.WriteAllBytes(filePath, file);\n\n        _converter.Quit();\n    }\n\n    public static void Main(string[] args)\n    {\n        string htmlContent = "<html><body><h1>Hello World</h1></body></html>";\n        ConvertHtmlToFile(htmlContent, "output.pdf");\n    }\n}