using System;\nusing System.Drawing;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "path_to_your_image.jpg"; // Replace with your image path\n\n        try\n        {\n            using (Image image = Image.FromFile(filePath))\n            {\n                // The image is now loaded into the 'image' variable.\n                // You can perform operations on it here, such as displaying or modifying.\n\n                Console.WriteLine($"Image loaded: {image.Width}x{image.Height}");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error loading image: " + ex.Message);\n        }\n    }\n}