// Create an instance of HttpClientHandler configured for Windows Authentication\nvar handler = new HttpClientHandler\n{\n    Credentials = CredentialCache.DefaultCredentials,\n    UseDefaultCredentials = true,\n    PreAuthenticate = true, // This forces authentication on all requests\n};\n\n// Instantiate HttpClient using the configured handler\nusing (var client = new HttpClient(handler))\n{\n    try\n    {\n        // Send a GET request to your resource\n        HttpResponseMessage response = await client.GetAsync("http://yourserver/yourfile");\n        response.EnsureSuccessStatusCode(); // Throws if not a success code.\n\n        string responseBody = await response.Content.ReadAsStringAsync();\n        Console.WriteLine(responseBody);\n    }\n    catch (HttpRequestException e)\n    {\n        Console.WriteLine($"Request error: {e.Message}");\n    }\n}