using NHibernate;\nusing System;\n\npublic static class QueryExtensions\n{\n    public static void SetInt32(this IQuery query, int position, int? val)\n    {\n        if (val.HasValue)\n        {\n            query.SetInt32(position, val.Value);\n        }\n        else\n        {\n            // Explicitly specify the type for the parameter\n            query.SetParameter(position, null, NHibernate.NHibernateUtil.Int32);\n        }\n    }\n\n    public static void SetInt32(this IQuery query, string name, int? val)\n    {\n        if (val.HasValue)\n        {\n            query.SetInt32(name, val.Value);\n        }\n        else\n        {\n            // Explicitly specify the type for the parameter\n            query.SetParameter(name, null, NHibernate.NHibernateUtil.Int32);\n        }\n    }\n\n    public static void SetDateTime(this IQuery query, int position, DateTime? val)\n    {\n        if (val.HasValue)\n        {\n            query.SetDateTime(position, val.Value);\n        }\n        else\n        {\n            // Explicitly specify the type for the parameter\n            query.SetParameter(position, null, NHibernate.NHibernateUtil.DateTime);\n        }\n    }\n\n    public static void SetDateTime(this IQuery query, string name, DateTime? val)\n    {\n        if (val.HasValue)\n        {\n            query.SetDateTime(name, val.Value);\n        }\n        else\n        {\n            // Explicitly specify the type for the parameter\n            query.SetParameter(name, null, NHibernate.NHibernateUtil.DateTime);\n        }\n    }\n\n    public static void SetString(this IQuery query, int position, string? val)\n    {\n        if (val != null)\n        {\n            query.SetString(position, val);\n        }\n        else\n        {\n            // Explicitly specify the type for the parameter\n            query.SetParameter(position, null, NHibernate.NHibernateUtil.String);\n        }\n    }\n\n    public static void SetString(this IQuery query, string name, string? val)\n    {\n        if (val != null)\n        {\n            query.SetString(name, val);\n        }\n        else\n        {\n            // Explicitly specify the type for the parameter\n            query.SetParameter(name, null, NHibernate.NHibernateUtil.String);\n        }\n    }\n}