using System.Xml;\n\n// Serialize method\npublic void SerializeBlogsToXml(BlogContext context, string outputPath) {\n    var blogs = context.Blogs.Include("Posts").ToList(); // Include related Posts\n\n    XmlSerializer serializer = new XmlSerializer(typeof(List<Blog>));\n\n    using (StringWriter writer = new StringWriter()) {\n        using (XmlTextWriter xmlWriter = XmlWriter.Create(writer)) {\n            serializer.Serialize(xmlWriter, blogs);\n            File.WriteAllText(outputPath, writer.ToString());\n        }\n    }\n}\n\n// Usage\nusing (var context = new BlogContext())\n{\n    SerializeBlogsToXml(context, "Blogs.xml");\n}