protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Store the original text in ViewState\n        ViewState["OriginalLabelText"] = myLabel.Text;\n        \n        // Alter the label's text as needed\n        myLabel.Text = "Altered Text";\n    }\n}\n\nprotected string GetOriginalLabelText()\n{\n    return ViewState["OriginalLabelText"] as string ?? string.Empty;\n}