using System;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assuming you have your Stream object as dataStream\n        using (var dataStream = response.GetResponseStream())\n        {\n            // Load the XML from the stream into an XDocument\n            XDocument doc = XDocument.Load(dataStream);\n            \n            // Define the XML namespace\n            XNamespace ns = "urn:betfair:games:api:v1";\n            \n            // Query for the properties within each object\n            var properties = \n                from channel in doc.Descendants(ns + "channel")\n                from game in channel.Descendants(ns + "game")\n                from gameData in game.Descendants(ns + "gameData")\n                from obj in gameData.Descendants(ns + "object")\n                from prop in obj.Descendants(ns + "property")\n                select new\n                {\n                    Name = (string)prop.Attribute("name"),\n                    Value = (string)prop.Attribute("value")\n                };\n\n            // Output the properties\n            foreach (var prop in properties)\n            {\n                Console.WriteLine($"Property: {prop.Name}, Value: {prop.Value}");\n            }\n        }\n    }\n}