public class WebMessageFormUrlEncodedBehavior : IEndpointBehavior\n{\n    public void AddBindingParameters(ServiceEndpoint endpoint, BindingParameterCollection bindingParameters)\n    {\n        // No additional parameters needed.\n    }\n\n    public void ApplyClientBehavior(ServiceEndpoint endpoint, ClientRuntime clientRuntime)\n    {\n        // Not required on the client side.\n    }\n\n    public void ApplyDispatchBehavior(ServiceEndpoint endpoint, EndpointDispatcher endpointDispatcher)\n    {\n        foreach (var operation in endpoint.Contract.Operations)\n        {\n            var dispatchOperation = endpointDispatcher.DispatchRuntime.Operations.Find(operation.Name);\n            if (operation.Messages[0].Action == "http://tempuri.org/IService1/Query_Post")\n            {\n                dispatchOperation.Formatter = new FormUrlEncodedFormatter(WebContentFormat.Json);\n            }\n        }\n    }\n\n    public void Validate(ServiceEndpoint endpoint)\n    {\n        // No validation needed.\n    }\n}