using System;\n\nclass Program\n{\n    static void Main()\n    {\n        char[] array1 = { 'A', 'B', 'C', 'D', 'E', 'F', 'G' };\n        int[] array2 = { 1, 2, 3, 4 };\n\n        object[] mixedArray = MixArrays(array1, array2);\n\n        Console.WriteLine(string.Join(", ", mixedArray));\n    }\n\n    static object[] MixArrays(char[] array1, int[] array2)\n    {\n        int maxLength = Math.Max(array1.Length, array2.Length);\n        var result = new object[maxLength * 2];\n\n        int index = 0;\n        for (int i = 0; i < maxLength; i++)\n        {\n            if (i < array1.Length) result[index++] = array1[i];\n            if (i < array2.Length) result[index++] = array2[i];\n        }\n\n        Array.Resize(ref result, index);\n        return result;\n    }\n}