// Define the task "Clean"\nTask("Clean")\n    .Does(() =>\n{\n    CleanDirectory("./artifacts");\n});\n\n// Define the task "Build"\nTask("Build")\n    .IsDependentOn("Clean")\n    .Does(() =>\n{\n    DotNetCoreBuild("./src/MyProject.csproj", new DotNetCoreBuildSettings\n    {\n        Configuration = "Release"\n    });\n});\n\n// Define the task "Pack"\nTask("Pack")\n    .IsDependentOn("Build")\n    .Does(() =>\n{\n    DotNetCorePack("./src/MyProject.csproj", new DotNetCorePackSettings\n    {\n        OutputDirectory = "./artifacts",\n        Configuration = "Release"\n    });\n});\n\n// Execute the tasks defined above.\nRunTarget("Pack");