using System;\n   using Microsoft.Scripting.Hosting;\n\n   class Program\n   {\n       static void Main()\n       {\n           // Create a runtime instance for IronRuby\n           ScriptEngine engine = Python.CreateEngine();\n\n           // Create scope to hold variables and return value from script execution\n           ScriptScope scope = engine.CreateScope();\n\n           // Load and execute the Ruby script\n           string fileName = "metadata.rb";\n           var metaDataHash = engine.ExecuteFile(fileName, scope);\n\n           // Access data returned from the script\n           Console.WriteLine("Name: " + Convert.ToString(metaDataHash["name"]));\n           Console.WriteLine("Version: " + Convert.ToString(metaDataHash["version"]));\n\n           dynamic features = metaDataHash["features"];\n           Console.Write("Features: ");\n           foreach (dynamic feature in features)\n           {\n               Console.Write(Convert.ToString(feature) + ", ");\n           }\n       }\n   }