// Receive encrypted ticket string from web service and set cookie\nstring encryptedTicket = CustomAuthenticationService.AuthenticateUser("username", "password");\n\nif (encryptedTicket != null)\n{\n    HttpCookie authCookie = new HttpCookie(FormsAuthentication.FormsCookieName, encryptedTicket);\n    HttpContext.Current.Response.Cookies.Add(authCookie);\n\n    // Redirect or continue with authenticated session\n}\nelse\n{\n    // Handle authentication failure\n}