using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("XInput1_4.dll", CharSet = CharSet.Auto, CallingConvention = CallingConvention.Cdecl)]\n    public static extern int XInputGetState(int dwUserIndex, ref XINPUT_STATE pState);\n\n    public struct XINPUT_GAMEPAD\n    {\n        public ushort wButtons;\n        public byte bLeftTrigger;\n        public byte bRightTrigger;\n        public short sThumbLX;\n        public short sThumbLY;\n        public short sThumbRX;\n        public short sThumbRY;\n    }\n\n    public struct XINPUT_STATE\n    {\n        public uint dwPacketNumber;\n        public XINPUT_GAMEPAD Gamepad;\n    }\n\n    static void Main()\n    {\n        XINPUT_STATE state = new XINPUT_STATE();\n        int result = XInputGetState(0, ref state);\n        \n        if (result == 0) // SUCCESS\n        {\n            Console.WriteLine("Game controller is connected!");\n            Console.WriteLine($"Left stick Y: {state.Gamepad.sThumbLY}");\n            // You can add more code to process the input as needed.\n        }\n        else\n        {\n            Console.WriteLine("No game controller detected.");\n        }\n    }\n}